import { AICoaching } from '@ai-coaching/sdk';

const coach = new AICoaching({
  apiKey: 'your-api-key',
  userId: 'user-123'
});

// Start a coaching session
const session = await coach.startSession({
  type: 'goal-setting',
  duration: 30 // minutes
});

// Session started successfully
return session.id;
